import React, { Component } from "react";
import axios from 'axios';

const SERVER_URL = "http://localhost:3000/airplanes.json";

class Airplanes extends Component {
  constructor() {
    super();
    console.log('constructor()');
    // TODO: fetch these via AJAX
    this.state = {
      airplanes: [],
    };
  }

  componentDidMount(){
    // Polling;
    // TODO: continuously fetch new airplanes
    const fetchAirplanes=()=>{
      axios.get(SERVER_URL).then((results) => {
        this.setState({airplanes: results.data});
        setTimeout(fetchAirplanes, 4000); // use recursion to call itself
      });
      this.saveSecret = this.saveSecret.bind(this);
    };

    fetchAirplanes();
  }

  saveSecret(content) {
    // convert content into a a secret object
    axios.post(SERVER_URL, {content: content}).then((response)=>{
      this.setState({airplanes: [...this.state.airplanes, response.data]});
    });
  }

  render() {
    return (
      <div>
        <h1>Tell me all your airplanes</h1>
        <SecretForm onSubmit={this.saveSecret} />
        <AirplanesList airplanes={this.state.airplanes} />
      </div>
    );
  }
}

class SecretForm extends Component {
  constructor() {
    super();
    this.state = { content: "" };
    this._handleChange = this._handleChange.bind(this);
    this._handleSubmit = this._handleSubmit.bind(this);
  }

  _handleChange(event) {
    this.setState({ content: event.target.value });
  }

  _handleSubmit(event) {
    event.preventDefault();
    this.props.onSubmit(this.state.content);
    this.setState({ content: "" });
  }

  render() {
    return (
      <form onSubmit={this._handleSubmit}>
        <textarea onChange={this._handleChange} value={this.state.content} />
        <input type="submit" value="Tell" />
      </form>
    );
  }
}

const AirplanesList = (props) => {
  return (
    <div>
      {props.airplanes.map((s) => (
        <p key={s.id}>{s.content}</p>
      ))}
    </div>
  );
};

export default Airplanes;
